import{_ as a,r as o,o as s,c as p,b as e,d as t,e as n,a as i}from"./app.234ff118.js";const l={},c=i('<h1 id="rbac\u6743\u9650\u6A21\u578B" tabindex="-1"><a class="header-anchor" href="#rbac\u6743\u9650\u6A21\u578B" aria-hidden="true">#</a> RBAC\u6743\u9650\u6A21\u578B</h1><p>[toc]</p><h1 id="_1-\u6982\u5FF5" tabindex="-1"><a class="header-anchor" href="#_1-\u6982\u5FF5" aria-hidden="true">#</a> 1.\u6982\u5FF5</h1><p><strong>Role-based Access Control\uFF0C<strong><strong>\u57FA\u4E8E\u89D2\u8272\u7684\u6743\u9650\u63A7\u5236\u6A21\u578B</strong></strong>\u3002</strong></p><p>**\u987E\u540D\u601D\u4E49\uFF0C\u7ED9\u7528\u6237\u5B9A\u4E49\u89D2\u8272\uFF0C\u901A\u8FC7\u89D2\u8272\u6765\u63A7\u5236\u6743\u9650\u3002**\u76EE\u524D\u6765\u8BF4\u57FA\u4E8E\u89D2\u8272\u6743\u9650\u63A7\u5236\u6A21\u578B\u662F\u5E94\u7528\u8F83\u5E7F\u7684\u4E00\u4E2A\u3002\u7279\u522B\u662F2B\u65B9\u5411SAAS\u9886\u57DF\uFF0C\u5E94\u7528\u5C24\u5176\u5E38\u89C1\u3002</p><p><img src="https://file.bbzy.online/blog/x2KT_efNMIBCdxl70IZsOv9TAMWQPhSA8zhxVSfz-Rg.png" alt="image"></p><p>\u5982\u4E0A\u56FE\u793A\uFF0C\u7528\u6237\u62E5\u6709\u89D2\u8272\uFF0C\u4E14\u53EF\u62E5\u6709\u591A\u4E2A\u89D2\u8272\uFF0C\u800C\u6BCF\u4E2A\u89D2\u8272\u5BF9\u5E94\u4E0D\u540C\u6743\u9650\u3002</p><p>\u8FD9\u6837\u7684\u597D\u5904\u662F\uFF1A\u4E0D\u5FC5\u4E3A\u6BCF\u4E00\u4E2A\u7528\u6237\u53BB\u914D\u7F6E\u6743\u9650\uFF0C\u62E5\u6709\u6781\u5927\u7684\u7075\u6D3B\u6027\u548C\u4FBF\u5229\u6027\u3002\u800C\u5F53\u7528\u6237\u53CA\u6743\u9650\u7684\u91CF\u7EA7\u53C8\u5927\u5230\u53E6\u4E00\u4E2A\u7EA7\u522B\u65F6\uFF0C\u53C8\u5F15\u5165\u4E86\u89D2\u8272\u7EC4\u548C\u6743\u9650\u7EC4\u6982\u5FF5\u3002</p><h1 id="_2-\u5229\u7528rbac\u6A21\u578B\u6765\u8FDB\u884C\u6743\u9650\u4F53\u7CFB\u7684\u8BBE\u8BA1" tabindex="-1"><a class="header-anchor" href="#_2-\u5229\u7528rbac\u6A21\u578B\u6765\u8FDB\u884C\u6743\u9650\u4F53\u7CFB\u7684\u8BBE\u8BA1" aria-hidden="true">#</a> 2.\u5229\u7528RBAC\u6A21\u578B\u6765\u8FDB\u884C\u6743\u9650\u4F53\u7CFB\u7684\u8BBE\u8BA1</h1><p>\u9996\u5148\u662F\uFF1A\u7528\u6237\u3001\u89D2\u8272\u3001\u6743\u9650\u3002</p><p>\u800C\u6743\u9650\uFF0C\u5177\u4F53\u5230\u67D0\u4E2A\u8F6F\u4EF6\u6765\u8BF4\uFF0C\u5B9E\u9645\u4E0A\u5305\u542B\u4E24\u4E2A\u65B9\u9762\u3002\u4E00\u4E2A\u662F\u83DC\u5355\u6743\u9650\uFF0C\u53E6\u4E00\u4E2A\u662F\u6570\u636E\u6743\u9650\u3002</p><p><img src="https://file.bbzy.online/blog/Yl3NoLmQEUceqb01fudKnOF_ofOJM67LR00nZOzM7ko.png" alt="image"></p><h1 id="_3-abac\u6743\u9650\u6A21\u578B\u7684\u7B80\u5355\u8BF4\u660E" tabindex="-1"><a class="header-anchor" href="#_3-abac\u6743\u9650\u6A21\u578B\u7684\u7B80\u5355\u8BF4\u660E" aria-hidden="true">#</a> 3.ABAC\u6743\u9650\u6A21\u578B\u7684\u7B80\u5355\u8BF4\u660E</h1><p>ABAC\u5230\u5E95\u662F\u4EC0\u4E48\u5462\uFF1F\u5B83\u5B58\u5728\u7684\u610F\u4E49\u5728\u54EA\u91CC\uFF1F\u5173\u4E8E\u672A\u6765\u7684\u6743\u9650\u8BBE\u8BA1\u8D8B\u52BF\uFF0C\u5B83\u80FD\u5E26\u7ED9\u6211\u4EEC\u4EC0\u4E48\u542F\u53D1\u5462\uFF1F</p><p><strong>ABAC\uFF0CAttribute-based Access Control</strong>. \u57FA\u4E8E\u5C5E\u6027\u7684\u8BBF\u95EE\u63A7\u5236\u3002\u800C\u5C5E\u6027\uFF0C\u603B\u7684\u6765\u8BF4\u6709\u4E09\u7C7B\uFF1A\u7528\u6237\u5C5E\u6027\u3001\u7CFB\u7EDF\u6216\u5E94\u7528\u88AB\u8BBF\u95EE\u5C5E\u6027\uFF08\u6570\u636E\u548C\u64CD\u4F5C\uFF09\u3001\u73AF\u5883\u5C5E\u6027\u3002</p><p>\u4E5F\u5C31\u662F\u8BF4\uFF0C\u7CFB\u7EDF\u6839\u636E\u4E00\u7EC4\u6216\u591A\u7EC4\u5C5E\u6027\u662F\u5426\u6EE1\u8DB3\u9884\u8BBE\u89C4\u5219\u6765\u52A8\u6001\u7684\u63A7\u5236\uFF0C\u8C01\u53EF\u4EE5\u8BBF\u95EE\u54EA\u4E9B\u529F\u80FD\u6570\u636E\u548C\u64CD\u4F5C\u3002RBAC\u6A21\u578B\uFF0C\u5176\u5B9E\u53EF\u4EE5\u770B\u6210\u662F\u9759\u6001\u7684\u3001\u5355\u7EC4\u5C5E\u6027\u7684ABAC\u6A21\u578B\u3002</p><p>BAC\u5219\u662F\u901A\u8FC7\u52A8\u6001\u8BA1\u7B97\u4E00\u4E2A\u6216\u4E00\u7EC4\u5C5E\u6027\u6765\u662F\u5426\u6EE1\u8DB3\u67D0\u79CD\u6761\u4EF6\u6765\u8FDB\u884C\u6388\u6743\u5224\u65AD\uFF08\u53EF\u4EE5\u7F16\u5199\u7B80\u5355\u7684\u903B\u8F91\uFF09\u3002\u5C5E\u6027\u901A\u5E38\u6765\u8BF4\u5206\u4E3A\u56DB\u7C7B\uFF1A\u7528\u6237\u5C5E\u6027\uFF08\u5982\u7528\u6237\u5E74\u9F84\uFF09\uFF0C\u73AF\u5883\u5C5E\u6027\uFF08\u5982\u5F53\u524D\u65F6\u95F4\uFF09\uFF0C\u64CD\u4F5C\u5C5E\u6027\uFF08\u5982\u8BFB\u53D6\uFF09\u548C\u5BF9\u8C61\u5C5E\u6027\uFF08\u5982\u4E00\u7BC7\u6587\u7AE0\uFF0C\u53C8\u79F0\u8D44\u6E90\u5C5E\u6027\uFF09\uFF0C\u6240\u4EE5\u7406\u8BBA\u4E0A\u80FD\u591F\u5B9E\u73B0\u975E\u5E38\u7075\u6D3B\u7684\u6743\u9650\u63A7\u5236\uFF0C\u51E0\u4E4E\u80FD\u6EE1\u8DB3\u6240\u6709\u7C7B\u578B\u7684\u9700\u6C42\u3002</p><p>\u7528\u4F8B\u5B50\u6765\u7406\u89E3\u8FD9\u4E2A\u6A21\u578B\u5C31\u662F\uFF1A\u53EA\u6709\u5F53\u7528\u6237\u89D2\u8272\u4E3AAdmin\uFF0C\u5728\u5DE5\u4F5C\u65F6\u95F4\u5185\uFF0C\u4E14\u5904\u5728C\u680B\u5927\u697CB\u5B9E\u9A8C\u5BA4\uFF0C\u624D\u53EF\u4EE5\u8BBF\u95EED\u6587\u4EF6\u3002</p><p>\u5B9E\u9645\u4E0A\uFF0CABAC\u662F\u4E2A\u53EF\u4EE5\u4EE5\u6700\u7EC6\u9897\u7C92\u5EA6\u6765\u7BA1\u7406\u6743\u9650\u7684\u6A21\u578B\u3002\u5B83\u53EF\u4EE5\u8BA9\u8BBE\u8BA1\u8005\uFF0C\u5229\u7528\u4EFB\u4F55\u4E00\u4E2A\u7528\u6237\u5C5E\u6027\u3001\u73AF\u5883\u5C5E\u6027\uFF0C\u6216\u8005\u591A\u4E2A\u5C5E\u6027\u4E4B\u95F4\u7684\u4EA4\u96C6\u3001\u5E76\u96C6\u7B49\u6765\u7EC4\u5408\u51FA\u52A8\u6001\u7684\u6743\u9650\u5224\u65AD\u903B\u8F91\u3002</p><p><strong>\u7ED3\u8BBA</strong></p><ul><li>\u96C6\u4E2D\u5316\u7BA1\u7406</li><li>\u53EF\u4EE5\u6309\u9700\u5B9E\u73B0\u4E0D\u540C\u9897\u7C92\u5EA6\u7684\u6743\u9650\u63A7\u5236</li><li>\u4E0D\u9700\u8981\u9884\u5B9A\u4E49\u5224\u65AD\u903B\u8F91\uFF0C\u51CF\u8F7B\u4E86\u6743\u9650\u7CFB\u7EDF\u7684\u7EF4\u62A4\u6210\u672C\uFF0C\u7279\u522B\u662F\u5728\u9700\u6C42\u7ECF\u5E38\u53D8\u5316\u7684\u7CFB\u7EDF\u4E2D</li><li>\u5B9A\u4E49\u6743\u9650\u65F6\uFF0C\u4E0D\u80FD\u76F4\u89C2\u770B\u51FA\u7528\u6237\u548C\u5BF9\u8C61\u95F4\u7684\u5173\u7CFB</li><li>\u89C4\u5219\u5982\u679C\u7A0D\u5FAE\u590D\u6742\u4E00\u70B9\uFF0C\u6216\u8005\u8BBE\u8BA1\u6DF7\u4E71\uFF0C\u4F1A\u7ED9\u7BA1\u7406\u8005\u7EF4\u62A4\u548C\u8FFD\u67E5\u5E26\u6765\u9EBB\u70E6</li><li>\u6743\u9650\u5224\u65AD\u9700\u8981\u5B9E\u65F6\u6267\u884C\uFF0C\u89C4\u5219\u8FC7\u591A\u4F1A\u5BFC\u81F4\u6027\u80FD\u95EE\u9898</li></ul><p><strong>\u5176\u4ED6</strong></p><p>\u65E2\u7136ABAC\u8FD9\u4E48\u597D\uFF0C\u90A3\u6700\u6D41\u884C\u7684\u4E3A\u4EC0\u4E48\u8FD8\u662FRBAC\u5462\uFF1F</p><p>\u6211\u8BA4\u4E3A\u4E3B\u8981\u8FD8\u662F\u56E0\u4E3A\u5927\u90E8\u5206\u7CFB\u7EDF\u5BF9\u6743\u9650\u63A7\u5236\u5E76\u6CA1\u6709\u8FC7\u591A\u7684\u9700\u6C42\uFF0C\u800C\u4E14ABAC\u7684\u7BA1\u7406\u76F8\u5BF9\u6765\u8BF4\u592A\u590D\u6742\u4E86\u3002</p>',24),h={href:"https://link.jianshu.com/?t=http://blog.kubernetes.io/2017/04/rbac-support-in-kubernetes.html",target:"_blank",rel:"noopener noreferrer"},d=e("code",null,"1.8",-1),_={href:"https://link.jianshu.com/?t=http://blog.kubernetes.io/2017/04/rbac-support-in-kubernetes.html",target:"_blank",rel:"noopener noreferrer"};function b(A,g){const r=o("ExternalLinkIcon");return s(),p("div",null,[c,e("p",null,[e("a",h,[t("Kubernetes\u4FBF\u56E0\u4E3AABAC\u592A\u96BE\u7528\uFF0C\u5728"),n(r)]),d,e("a",_,[t("\u7248\u672C\u91CC\u5F15\u5165\u4E86RBAC\u7684\u65B9\u6848"),n(r)]),t("\u3002")])])}const B=a(l,[["render",b],["__file","RBAC\u6743\u9650\u6A21\u578B.html.vue"]]);export{B as default};
